generated.css:
* {
  padding: 0;
  margin: 0;
  border: 0;
}

*, *:before, *:after {
  -moz-box-sizing: border-box;
  -webkit-box-sizing: border-box;
  box-sizing: border-box;
}

:focus, :active {
  outline: none;
}

a:focus, a:active {
  outline: none;
}

nav, footer, header, aside {
  display: block;
}

html, body {
  height: 100%;
  width: 100%;
  font-size: 100%;
  line-height: 1;
  font-size: 14px;
  -ms-text-size-adjust: 100%;
  -moz-text-size-adjust: 100%;
  -webkit-text-size-adjust: 100%;
}

input, button, textarea {
  font-family: inherit;
}

input::-ms-clear {
  display: none;
}

button {
  cursor: pointer;
}

button::-moz-focus-inner {
  padding: 0;
  border: 0;
}

a, a:visited {
  text-decoration: none;
}

a:hover {
  text-decoration: none;
}

ul li {
  list-style: none;
}

img {
  vertical-align: top;
}

h1, h2, h3, h4, h5, h6 {
  font-size: inherit;
  font-weight: 400;
}

/* ============header=============== */
.container {
  max-width: 1470px;
  margin: 0 auto;
  padding: 75px 15px;
}

a {
  text-decoration: none;
  color: white;
}

header {
  width: 100%;
  color: white;
  background-color: #030303;
  padding-bottom: 75px;
}
header .header {
  padding: 0 15px;
  position: relative;
}
header .header__banner {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}
header .header__menu-container {
  z-index: 9999;
  max-width: 1470px;
  height: 150px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}
header .header__logo {
  font-family: "Forum";
  font-weight: 400;
  font-size: 28px;
  line-height: 31px;
  letter-spacing: 5px;
  text-transform: uppercase;
  color: #FFFFFF;
  position: relative;
  padding-left: 75px;
  z-index: 1;
}
header .header__logo::before {
  content: "";
  background: url(/a7b19ffe762c49facb9f54d1e9a9c890.png) no-repeat;
  color: #FFFFFF;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  z-index: 55;
}
header .header__menu {
  max-width: 776px;
  display: flex;
  justify-content: space-between;
}
header .header__menu__list {
  padding-right: 55px;
  font-family: "Roboto";
  font-weight: 100;
  font-size: 22px;
  line-height: 26px;
  letter-spacing: 2px;
  color: #FFFFFF;
}
header .header__menu__list:last-child {
  padding-right: 0px;
}
header .header__menu a {
  transition: 0.5s;
}
header .header__menu__list a:hover {
  color: #9d8665;
}
header .header__burger {
  display: none;
}
header .banner__container {
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 750px;
  position: relative;
}
header .banner__container picture {
  max-width: 1000px;
  position: absolute;
  top: 0;
  right: 0;
  z-index: 0;
}
header .banner__container picture img {
  width: 100%;
  object-fit: cover;
  transition: 0.5s;
}
header .banner__container .banner__text {
  padding-top: 150px;
  max-width: 600px;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 3;
}
header .banner__container .banner__text__title {
  font-family: "Forum";
  font-weight: 400;
  font-size: 80px;
  line-height: 90px;
  letter-spacing: 5px;
  text-transform: uppercase;
  color: #FFFFFF;
  margin-bottom: 25px;
}
header .banner__container .banner__text__subtitle {
  font-family: "Roboto";
  font-weight: 300;
  font-size: 28px;
  line-height: 33px;
  letter-spacing: 2px;
  color: #FFFFFF;
  margin-bottom: 75px;
}
header .banner__container .banner__text__button {
  padding: 25px 64px;
  background: #710707;
  border-radius: 10px;
  font-family: "Roboto";
  font-weight: 300;
  font-size: 22px;
  line-height: 26px;
  letter-spacing: 2px;
  color: #FFFFFF;
  transition: 0.7s;
}
header .banner__container .banner__text__button:hover {
  background: #9d8665;
}
header .banner__container .banner__text__button:hover {
  position: relative;
  animation-name: button;
  animation-duration: 1.5s;
  animation-timing-function: linear;
  animation-iteration-count: infinite;
  animation-direction: alternate;
  background: #9d8665;
}
@keyframes button {
  0% {
    left: 0%;
  }
  100% {
    left: 10%;
  }
}
header .banner__navigation {
  padding: 15px 55px;
  max-width: 600px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-family: "Roboto";
  font-weight: 400;
  font-size: 22px;
  line-height: 40px;
  text-transform: uppercase;
  color: #FFFFFF;
  background: #030303;
  position: absolute;
  right: 10px;
  bottom: -60px;
  height: 120px;
  z-index: 4;
}
header .banner__navigation__pages {
  display: flex;
  max-width: 75px;
}
header .banner__navigation__page1, header .banner__navigation__page2 {
  padding-right: 10px;
}
header .banner__navigation__page2 {
  padding-left: 10px;
  border-left: 1px solid white;
}
header .banner__navigation__dots {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: 0 55px;
}
header .banner__navigation__dot {
  width: 16px;
  height: 16px;
  left: 646px;
  top: 742px;
  background: #FFFFFF;
  margin: 0 15px;
}
header .banner__navigation__dot:hover {
  background: #9d8665;
  cursor: pointer;
}
header .banner__navigation__item {
  height: inherit;
  display: flex;
  align-items: center;
}
header .banner__navigation__previous {
  width: 30px;
  height: 8px;
  background: url(/051763cfc3aae7687b1ca88597ace7b5.png) no-repeat;
  cursor: pointer;
}
header .banner__navigation__next {
  width: 30px;
  height: 8px;
  background: url(/b2b21862b00a400508d2081dc5bc0cf1.png) no-repeat;
  margin-left: 20px;
  cursor: pointer;
}

/* ============================Visual tour================= */
.tour__container {
  display: flex;
  flex-direction: column;
}
.tour__title {
  font-family: "Forum";
  font-weight: 400;
  font-size: 80px;
  line-height: 90px;
  letter-spacing: 5px;
  text-transform: uppercase;
  color: #9d8665;
  padding-bottom: 25px;
}
.tour__line {
  max-width: 1470px;
  height: 3px;
  background: #710707;
}
.tour__items {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(440px, 1fr));
  column-gap: 60px;
  align-items: center;
  justify-items: center;
}
.tour .tour__item {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  padding-top: 45px;
  padding: 5px;
  margin-top: 45px;
  max-width: 440px;
  transition: 0.7s;
}
.tour .tour__item:hover {
  transform: scale(1.1);
  box-shadow: 10px 5px 20px #9d8665;
  cursor: pointer;
}
.tour .tour__item__title {
  flex-shrink: 1;
  padding-top: 25px;
  font-family: "Forum";
  font-weight: 400;
  font-size: 32px;
  line-height: 35px;
  letter-spacing: 5px;
  text-transform: uppercase;
  color: #030303;
}
.tour .tour__item__line {
  margin-top: 10px;
  max-width: 300px;
  height: 2px;
  background: #710707;
}
.tour .tour__item__subtitle {
  padding: 10px 0;
  font-family: "Roboto";
  font-weight: 100;
  font-size: 22px;
  line-height: 26px;
  color: #030303;
}
.tour .tour__item img {
  width: 100%;
  object-fit: cover;
}

/* ===========================explore======================== */
.explore {
  background-color: #030303;
  color: white;
}
.explore__container {
  display: grid;
  grid-template-columns: repeat(2, minmax(440px, 1fr));
  align-items: center;
  justify-items: center;
}
.explore__text {
  max-width: 660px;
}
.explore__text__title {
  font-family: "Forum";
  font-weight: 400;
  font-size: 80px;
  line-height: 90px;
  letter-spacing: 5px;
  text-transform: uppercase;
  color: #FFFFFF;
  padding-bottom: 25px;
}
.explore__text__line {
  max-width: 660px;
  height: 3px;
  background: #710707;
  border-radius: 0px;
  margin-bottom: 75px;
}
.explore__text__subtitle {
  font-family: "Roboto";
  font-weight: 300;
  font-size: 22px;
  line-height: 38px;
  color: #FFFFFF;
}
.explore__text__subtitle span {
  color: #9d8665;
}
.explore__text__subtitle p {
  margin-bottom: 25px;
}

.explore__img {
  max-width: 720px;
  position: relative;
  top: 0;
  left: 0;
  z-index: 6;
  margin-left: 15px;
}
.explore__img::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  background: url(/582417741bae1be122f85cebd9504c58.png) 0 0/contain no-repeat;
  width: 100%;
  height: 100%;
  z-index: 55;
}
.explore__img::after {
  content: "";
  position: absolute;
  top: 0;
  left: 59%;
  background: url(/dc8f44271e4777ff65f99651d12489da.png) 0 0/contain no-repeat;
  width: 40px;
  height: 100%;
  z-index: 55;
  max-width: 720px;
  cursor: pointer;
}

.explore__img img {
  object-fit: cover;
  width: 100%;
  height: 100%;
}

/* ===================================video========================= */
.journey__text {
  display: flex;
  justify-content: space-between;
  padding-bottom: 75px;
}
.journey__title {
  max-width: 605px;
  font-family: "Forum";
  font-weight: 400;
  font-size: 80px;
  line-height: 90px;
  text-transform: uppercase;
  color: #9d8665;
}
.journey__subtitle {
  max-width: 720px;
  font-family: "Roboto";
  font-weight: 300;
  font-size: 22px;
  display: flex;
  align-items: center;
  color: #000000;
}
.journey__mainvideo {
  align-self: center;
  padding-bottom: 56%;
  position: relative;
  margin-bottom: 40px;
}
.journey__mainvideo iframe {
  position: absolute;
  top: 0;
  left: 0;
  object-fit: cover;
  height: 100%;
  width: 100%;
}
.journey__secondaryvideo {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(290px, 1fr));
  gap: 40px;
}
.journey .secondaryvideo {
  padding-bottom: 56%;
  position: relative;
}
.journey .journey__secondaryvideo iframe {
  position: absolute;
  top: 0;
  left: 0;
  object-fit: cover;
  height: 100%;
  width: 100%;
}

.video__navigation {
  margin-top: 40px;
  display: flex;
  justify-content: center;
  position: relative;
}
.video__navigation__stroke {
  padding: 0 10px;
}
.video__navigation__dot {
  display: inline-block;
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: #999999;
  margin: 0px 10px;
}
.video__navigation__dot:hover {
  background: #9d8665;
  cursor: pointer;
}

/* ======================================gallery=============== */
.gallery {
  background: #030303;
}
.gallery__container {
  height: 2290px;
  overflow: hidden;
}
.gallery__title {
  font-family: "Forum";
  font-weight: 400;
  font-size: 80px;
  line-height: 90px;
  letter-spacing: 5px;
  text-transform: uppercase;
  color: #FFFFFF;
  padding-bottom: 75px;
}
.gallery__img {
  display: grid;
  align-items: center;
  justify-content: center;
  grid-template-columns: repeat(auto-fit, minmax(456px, 1fr));
  row-gap: 24px;
  column-gap: 30px;
  align-items: center;
  justify-items: center;
}
.gallery__img div {
  max-width: 456px;
}
.gallery__img img {
  width: 100%;
  object-fit: cover;
}

/* ===================================form tickets======== */
.form__title {
  font-family: "Forum";
  font-weight: 400;
  font-size: 80px;
  line-height: 90px;
  letter-spacing: 5px;
  text-transform: uppercase;
  color: #9D8665;
}
.form__line {
  margin-top: 10px;
  margin-bottom: 75px;
  max-width: 1470px;
  height: 2px;
  background: #710707;
  border-radius: 0px;
}

.form__buy {
  display: flex;
  justify-content: space-between;
}
.form__buy__img {
  max-width: 720px;
}
.form__buy__img img {
  width: 100%;
  object-fit: cover;
}

.form__tickets {
  max-width: 690px;
  display: flex;
  justify-content: space-between;
  padding-left: 15px;
}

.form__radio__radio {
  margin-bottom: 25px;
}

.checkbox__label {
  font-family: "Roboto";
  font-style: normal;
  font-weight: 300;
  font-size: 22px;
  line-height: 26px;
  color: #000000;
}

.form__radio__title, .form__amount__title {
  font-family: "Roboto";
  font-weight: 700;
  font-size: 28px;
  line-height: 33px;
  color: #000000;
}

.form__amount {
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
}

.form__radio__title {
  margin-bottom: 30px;
}

.form__amount__title {
  align-self: flex-end;
  margin-bottom: 25px;
}

.checkbox {
  position: relative;
}

.checkbox__input {
  display: none;
}

.checkbox__label {
  padding: 8px 245px 0 45px;
  position: relative;
}

.checkbox__label::before {
  content: "";
  position: absolute;
  z-index: 3;
  top: 14px;
  left: 0;
  width: 17px;
  height: 17px;
  border-radius: 50%;
  border: 2px solid #000000;
  box-sizing: border-box;
}

.checkbox__label::after {
  content: "";
  position: absolute;
  z-index: 4;
  width: 7px;
  height: 7px;
  left: 5px;
  top: 19px;
  border-radius: 50%;
  background: #000000;
  opacity: 0;
}

.checkbox__input:checked + .checkbox__label::after {
  opacity: 1;
}

.form__amount__age18,
.form__amount__age65 {
  font-family: "Roboto";
  padding-top: 10px;
  font-weight: 300;
  font-size: 22px;
  line-height: 26px;
  color: #000000;
}

.buttom {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  height: 50px;
  border: 3px solid #000000;
}

.buttom1 input {
  text-align: center;
  max-width: 50px;
  border: none;
  font-weight: 300;
  font-size: 22px;
  line-height: 26px;
}

.buttom2 {
  display: flex;
  flex-direction: column;
}

.buttom2_2 button, .buttom2_1 button {
  background: white;
  border: none;
  width: 50px;
  height: 28.5px;
  font-size: 32px;
  font-weight: 700;
}

.form__amount__price {
  font-family: "Roboto";
  font-weight: 700;
  font-size: 28px;
  line-height: 33px;
  letter-spacing: 1.65px;
  color: #030303;
  margin-top: 50px;
}

.form__amount__button {
  margin-top: 60px;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 50px;
  left: 0px;
  top: 0px;
  background: #030303;
  border-radius: 2px;
  font-family: "Roboto";
  font-weight: 300;
  font-size: 22px;
  line-height: 26px;
  display: flex;
  align-items: center;
  text-align: center;
  letter-spacing: 2px;
  color: #FFFFFF;
  padding: 0 15px;
  transition: 0.5s;
}
.form__amount__button:hover {
  background: #9d8665;
  color: #030303;
  cursor: pointer;
}

/* =====================parallax================ */
.parallax {
  width: 100%;
}
.parallax img {
  width: 100%;
  object-fit: cover;
}

/* ==============contacts================== */
.contacts__title {
  font-family: "Forum";
  font-weight: 400;
  font-size: 80px;
  line-height: 90px;
  letter-spacing: 5px;
  text-transform: uppercase;
  color: #9D8665;
}
.contacts__line {
  margin-top: 10px;
  margin-bottom: 75px;
  max-width: 1470px;
  height: 2px;
  background: #710707;
  border-radius: 0px;
}
.contacts__body {
  margin-top: 75px;
  display: flex;
  justify-content: space-between;
}
.contacts__text h2 {
  max-width: 227px;
  font-family: "Roboto";
  font-weight: 900;
  font-size: 28px;
  line-height: 33px;
  color: #030303;
  margin-bottom: 50px;
}
.contacts__text p {
  max-width: 300px;
  font-family: "Roboto";
  font-weight: 300;
  font-size: 22px;
  line-height: 26px;
  color: #030303;
  padding-bottom: 25px;
}
.contacts__map {
  max-width: 960px;
  margin-bottom: 75px;
  margin-left: 15px;
}
.contacts__map img {
  width: 100%;
  object-fit: cover;
}

/* =========================footer================ */
footer {
  background: #030303;
}

.footer__navigation {
  border-bottom: 3px solid #666666;
}
.footer__navigation__container {
  padding: 75px 15px 60px 15px;
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
}
.footer__navigation__logo {
  font-family: "Forum";
  font-weight: 400;
  font-size: 28px;
  line-height: 31px;
  letter-spacing: 5px;
  text-transform: uppercase;
  color: #FFFFFF;
  position: relative;
  padding-left: 75px;
  z-index: 1;
}
.footer__navigation__logo::before {
  content: "";
  background: url(/a7b19ffe762c49facb9f54d1e9a9c890.png) no-repeat;
  color: #FFFFFF;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  z-index: 55;
}
.footer__navigation__text {
  margin: 0 15px;
  width: 300px;
  font-family: "Roboto";
  font-weight: 100;
  font-size: 22px;
  line-height: 26px;
  letter-spacing: 2px;
  color: #FFFFFF;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 15px;
}
.footer__navigation__text p {
  max-width: 85px;
}
.footer__navigation__text p:hover {
  color: #9d8665;
  cursor: pointer;
}
.footer__navigation__icons {
  margin-top: 25px;
  align-self: flex-end;
}

.icons {
  max-width: 500px;
  display: flex;
  justify-content: space-between;
}
.icons .icon {
  margin: 0 5px;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 48px;
  height: 48px;
  border: 1px solid #666666;
  box-sizing: border-box;
  filter: drop-shadow(0px 0px 0px #666666);
  border-radius: 50px;
}
.icons .icon img {
  width: 20px;
  height: 20px;
}

.copyrights__container {
  padding: 50px 15px;
  display: flex;
  justify-content: space-between;
}
.copyrights__container p {
  font-family: "Roboto";
  font-weight: 100;
  font-size: 22px;
  line-height: 22px;
  letter-spacing: 2px;
  color: #FFFFFF;
}

@media (max-width: 1024px) {
  header {
    padding-bottom: 0;
  }
  header .header {
    display: flex;
    flex-direction: column;
  }
  header .header__menu-container {
    position: relative;
    top: 0;
    left: 0;
    padding: 0px;
    height: 130px;
    z-index: 100;
  }
  header .header__menu {
    display: none;
  }
  header .header__menu__list {
    height: inherit;
    padding: 40px 55px;
  }
  header .header__burger {
    display: block;
    position: absolute;
    top: 38%;
    right: 0;
    width: 30px;
    height: 20px;
  }
  header .header__burger::before {
    content: "";
    background-color: white;
    position: absolute;
    width: 100%;
    height: 2px;
    left: 0;
    top: 0;
  }
  header .header__burger:after {
    content: "";
    background-color: white;
    position: absolute;
    width: 100%;
    height: 2px;
    left: 0;
    bottom: 0;
  }
  header .header__burger span {
    position: absolute;
    background-color: white;
    left: 0;
    top: 0;
    width: 100%;
    height: 2px;
    top: 9px;
  }
  header .header__burger .second-menu {
    border-radius: 5px;
    padding-top: 55px;
    position: absolute;
    display: flex;
    flex-direction: column;
    bottom: 0;
    right: 0;
    transform: translateY(100%);
    background: rgba(0, 0, 0, 0.774);
    font-size: 40px;
    opacity: 0;
    transition: 0.5s all ease-in;
  }
  header .header__burger .second-menu a {
    max-width: 1024px;
    display: block;
    text-align: center;
    padding: 20px 105px;
    transition: 0.5s all;
  }
  header .header__burger .second-menu a:hover {
    color: #9d8665;
  }
  header .header__burger #main-menu {
    appearance: none;
    transition: 0.5s all;
  }
  header .header__burger #main-menu:checked ~ .second-menu {
    opacity: 1;
  }
  header .banner__container {
    padding: 0 15px;
    flex-direction: column;
    justify-content: center;
    height: 750px;
  }
  header .banner__container .picture {
