sources.sass\\nulstyle.scss:
*{
	padding: 0;
	margin: 0;
	border: 0;
}

*,*:before,*:after{
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
}

:focus,:active{outline: none;}
a:focus,a:active{outline: none;}

nav,footer,header,aside{display: block;}

html,body{
	height: 100%;
	width: 100%;
	font-size: 100%;
	line-height: 1;
	font-size: 14px;
	-ms-text-size-adjust: 100%;
	-moz-text-size-adjust: 100%;
	-webkit-text-size-adjust: 100%;
}

input,button,textarea{font-family:inherit;}

input::-ms-clear{display: none;}
button{cursor: pointer;}
button::-moz-focus-inner {padding:0;border:0;}
a, a:visited{text-decoration: none;}
a:hover{text-decoration: none;}
ul li{list-style: none;}
img{vertical-align: top;}

h1,h2,h3,h4,h5,h6{font-size:inherit;font-weight: 400;}

sources.sass\\style.scss:
$black: #030303;
$red: #710707;
$gold: #9d8665;


@import 'nulstyle.scss';

/* ============header=============== */
.container {
	max-width: 1470px;
	margin: 0 auto;
	padding: 75px 15px;
}

a {
	text-decoration: none;
	color: white;
}

header {
	width: 100%;
	color: white;
	background-color: $black;
	padding-bottom: 75px;
	.header {
		padding: 0 15px;
		position: relative;
		&__banner {
			display: flex;
			flex-direction: column;
			justify-content: space-between;
		}
		&__menu-container {
			z-index: 9999;
			max-width: 1470px;
			height: 150px;
			display: flex;
			justify-content: space-between;
			align-items: center;
		}
		&__logo {
			font-family: 'Forum';
			font-weight: 400;
			font-size: 28px;
			line-height: 31px;
			letter-spacing: 5px;
			text-transform: uppercase;
			color: #FFFFFF;
			position: relative;
			padding-left: 75px;
			z-index: 1;
			&::before {
				content: "";
				background: url(../assets/logo.png) no-repeat;
				color: #FFFFFF;
				position: absolute;
				width: 100%;
				height: 100%;
				top: 0;
				left: 0;
				z-index:55;
			}
		}
		&__menu {
			max-width: 776px;
			display: flex;
			justify-content: space-between;
			&__list {
				padding-right: 55px;
				font-family: 'Roboto';
				font-weight: 100;
				font-size: 22px;
				line-height: 26px;
				letter-spacing: 2px;
				color: #FFFFFF;
				}
			&__list:last-child {
				padding-right: 0px;
			}
			a {
				transition: 0.5s;
			}
			&__list a:hover {
				color: $gold;
			}
		}
		&__burger {
			display: none;
		}
	}

.banner__container {
	display: flex;
	align-items: center;
	justify-content: space-between;
	height: 750px;
	position: relative;
	picture {
		max-width: 1000px;
		position: absolute;
		top: 0;
		right: 0;
		z-index: 0;
		& img {
			width: 100%;
			object-fit: cover;
			transition: 0.5s ;
		}
}

.banner__text {
	padding-top: 150px;
	max-width: 600px;
	position: absolute;
	top: 0;
	left: 0;
	z-index: 3;
	&__title {
		font-family: 'Forum';
		font-weight: 400;
		font-size: 80px;
		line-height: 90px;
		letter-spacing: 5px;
		text-transform: uppercase;
		color: #FFFFFF;
		margin-bottom: 25px;
		}
	&__subtitle {
		font-family: 'Roboto';
		font-weight: 300;
		font-size: 28px;
		line-height: 33px;
		letter-spacing: 2px;
		color: #FFFFFF;
		margin-bottom: 75px;
	}
	&__button {
		padding: 25px 64px;
		background: $red;
		border-radius: 10px;
		font-family: 'Roboto';
		font-weight: 300;
		font-size: 22px;
		line-height: 26px;
		letter-spacing: 2px;
		color: #FFFFFF;
		transition: 0.7s;

		}
		&__button:hover {
		background: $gold;
		}
		&__button:hover{
			position: relative;
			animation-name: button;
			animation-duration: 1.5s;
			animation-timing-function: linear;
			animation-iteration-count: infinite;
			animation-direction: alternate;
			background: $gold;
		}
	}
}
@keyframes button{
	0%{
	left: 0%;
	}
	100%{
	left: 10%;
	}
	}

	.banner__navigation {
		padding: 15px 55px;
		max-width: 600px; 
		display: flex;
		justify-content: space-between;
		align-items: center;
		font-family: 'Roboto';
		font-weight: 400;
		font-size: 22px;
		line-height: 40px;
		text-transform: uppercase;
		color: #FFFFFF;
		background: $black;
		position: absolute;
		right: 10px;
		bottom: -60px;
		height: 120px;
		z-index: 4;
		&__pages {
			display: flex;
			max-width: 75px;
		}
		&__page1, &__page2 {
			padding-right: 10px ;
		}
		&__page2 {
			padding-left: 10px ;
			border-left: 1px solid white;
		}
		&__dots {
			display: flex;
			justify-content: space-between;
			align-items: center;
			margin: 0 55px;
		}
		&__dot {
			width: 16px;
			height: 16px;
			left: 646px;
			top: 742px;
			background: #FFFFFF;
			margin: 0 15px;
			&:hover {
				background: $gold;
				cursor: pointer;
			}
		}
		&__item {
			height: inherit;
			display: flex;
			align-items: center;
		}
		&__previous {
			width: 30px;
			height: 8px;
			background: url(../assets/previous.png) no-repeat;
			cursor: pointer;
			}
		&__next{
			width: 30px;
			height: 8px;
			background: url(../assets/next.png) no-repeat;
			margin-left: 20px;
			cursor: pointer;
		}
	}
}
/* ============================Visual tour================= */
.tour {
	&__container {	
		display: flex;
		flex-direction: column;
	}
	&__title {
		font-family: 'Forum';
		font-weight: 400;
		font-size: 80px;
		line-height: 90px;
		letter-spacing: 5px;
		text-transform: uppercase;
		color: $gold;
		padding-bottom: 25px;
	}
	&__line {
		max-width: 1470px;
		height: 3px;
		background: $red;
	}
	&__items {
		display: grid;
		grid-template-columns:  repeat(auto-fit, minmax(440px, 1fr));
		column-gap: 60px;
		align-items: center;
		justify-items: center;
	}
	.tour__item {
		display: flex;
		flex-direction: column;
		justify-content: space-between;
		padding-top: 45px;
		padding: 5px;
		margin-top: 45px;
		max-width: 440px;
		transition: .7s;
		&:hover {
			transform: scale(1.1);
			box-shadow: 10px 5px 20px $gold;
			cursor: pointer;
		}
		&__title {
			flex-shrink: 1;
			padding-top: 25px;
			font-family: 'Forum';
			font-weight: 400;
			font-size: 32px;
			line-height: 35px;
			letter-spacing: 5px;
			text-transform: uppercase;
			color: $black;
		}
		&__line {
			margin-top: 10px;
			max-width: 300px;
			height: 2px;
			background: $red;
		}
		&__subtitle {
			padding:10px 0;
			font-family: 'Roboto';
			font-weight: 100;
			font-size: 22px;
			line-height: 26px;
			color: $black;
		}
	}
	.tour__item img{
		width: 100%;
		object-fit: cover;
	}
}

/* ===========================explore======================== */
.explore {
	background-color: $black;
	color: white;
	&__container{
		display: grid;
		grid-template-columns:  repeat(2, minmax(440px, 1fr));
		align-items: center;
		justify-items: center;
	}
	&__text {
		max-width: 660px;
	}
	&__text__title {
		font-family: 'Forum';
		font-weight: 400;
		font-size: 80px;
		line-height: 90px;
		letter-spacing: 5px;
		text-transform: uppercase;
		color: #FFFFFF;
		padding-bottom: 25px;
	}
	&__text__line {
		max-width: 660px;
		height: 3px;
		background: $red;
		border-radius: 0px;
		margin-bottom: 75px;
	}
	&__text__subtitle {
		font-family: 'Roboto';
		font-weight: 300;
		font-size: 22px;
		line-height: 38px;
		color: #FFFFFF;
	}
	&__text__subtitle span {
		color:$gold;
	}
	&__text__subtitle p {
		margin-bottom: 25px;
	}
}

.explore__img {
	max-width: 720px;
	position: relative;
	top: 0;
	left: 0;
	z-index: 6;
	margin-left: 15px;
	&::before {
		content: "";
		position: absolute;
		top: 0;
		left: 0;
		background: url(../assets/explore_2.png) 0 0/contain no-repeat;
		width: 100%;
		height: 100%;
		z-index: 55;
	}
	&::after {
		content: "";
		position: absolute;
		top: 0;
		left: 59%;
		background: url(../assets/explore_slider.png) 0 0/contain no-repeat;
		width: 40px;
		height: 100%;
		z-index: 55;
		max-width: 720px;
		cursor: pointer;
	}
	
}
.explore__img img {
	object-fit: cover;
	width: 100%;
	height: 100%;
}

/* ===================================video========================= */
.journey {
	&__text {
		display: flex;
		justify-content: space-between;
		padding-bottom: 75px;
	}
	&__title {
		max-width: 605px;
