	color: #000000;
}

.form__amount {
	display: flex;
	flex-direction: column;
	justify-content: flex-end;
}

.form__radio__title  {
	margin-bottom: 30px;
}

.form__amount__title  {
	align-self: flex-end;
	margin-bottom: 25px;
}

.checkbox {
	position: relative;
}

.checkbox__input {
display: none;
}
.checkbox__label {
	padding: 8px 245px 0 45px;
	position: relative;
}
.checkbox__label::before {
	content: "";
	position: absolute;
	z-index: 3;
	top: 14px;
	left: 0;
	width: 17px;
	height: 17px;
	border-radius: 50%;
	border: 2px solid #000000;
	box-sizing: border-box;
} 

.checkbox__label::after{
	content: "";
	position: absolute;
	z-index: 4;
	width: 7px;
	height: 7px;
	left: 5px;
	top: 19px;
	border-radius: 50%;
	background: #000000;
		opacity: 0;
}

.checkbox__input:checked + .checkbox__label::after {
	opacity: 1;
}

.form__amount__age18,
.form__amount__age65 {
	font-family: 'Roboto';
	padding-top:10px ;
	font-weight: 300;
	font-size: 22px;
