@media (max-width:768px) {
	header {
		.header {
			padding: 0;
			padding: 0 15px;

			&__menu-container {
				height: 110px;
			}
		}
	
		.banner__container {
			max-width: 768px;
			padding: 0;
			display:block;
			align-self: center;
			height: 750px;
			
			picture {
				align-items: center;
				width: 100%;
				position: relative;
				& img {
					padding: 0;
					width: 100%;
					object-fit: cover;
				}
			}

			.banner__text {
				padding: 0;
				text-align: center;
				position: static;
				max-width: 768px;

				&__title {
					font-size: 40px;
					line-height: 60px;
				}
				&__subtitle {
					font-size: 20px;
					line-height: 23px;
				}
				&__button {
				display: none;
				}
			}
		}
		.banner__navigation {
			position: static;
			justify-content: space-around;
			bottom: 0;
			padding: 0 15px;
			align-self: center;
			max-width: 420px;
			&__page1, &__page2 {
				padding-right: 5px ;
				font-size: 20px;
				line-height: 40px;
			}
			&__page2 {
				padding-left: 5px ;
				border-left: 1px solid white;
			}
			&__dots {
				display: flex;
				justify-content: space-between;
				align-items: center;
				margin: 0 35px;
			}
			&__dot {
				width: 12px;
				height: 12px;
				margin: 0 15px;
			}
			&__next{
				margin-left: 15px;
			}
		}
	}
	.tour {
		&__container {	
			max-width: 768px;
			padding: 40px 15px;
		}
		&__title {
			font-size: 40px;
			line-height: 60px;
			padding-bottom: 25px;
		}
		&__items {

			align-items: start;
			justify-items: center;
			column-gap: 56px;

		}
		
		.tour__item {
			padding-top: 30px;
			max-width: 280px;
				&__title {
				padding-top: 25px;
				font-size: 28px;
				line-height: 31px;
			}
			&__line {
				max-width: 150px;
			}
			&__subtitle {
				font-size: 18px;
				line-height: 21px;
			}
		}

	}
	.explore {
		&__container{
			padding: 40px 15px;
			display: flex;
			flex-direction: column;
		}
		&__text {
			max-width: 768px;
			margin-bottom: 0px;
			align-self: stretch;
		}
		&__text hr {
			width: 100%;
		}
		&__text__title {
			font-size: 40px;
			line-height: 60px;
			letter-spacing: 5px;
		}
		&__text__line {
			max-width: 768px;
			margin-bottom: 60px;
		}
		&__text__subtitle {
			display: none;
		}
		.explore__img {
			margin-left: 0;
		}
		.explore__img img {
			object-fit: cover;
		}
	}
	.journey {
		&__container {
			padding: 40px 15px;
		}
			&__text {
			margin-bottom: 40px;
			padding: 0;
			}
			&__title {
				font-size: 50px;
				line-height: 75px;
			}
			&__subtitle {
			display: none;
			}
		
			&__secondaryvideo {
				grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
				gap: 20px;
			}
				.secondaryvideo:last-child {
				display: none;
			}
	}
	.gallery {
		&__container{
			height: 1300px;
			max-width: 1024px;
			padding: 40px 15px;
		}
		&__title {
			font-size: 40px;
			line-height: 60px;
		}
		&__img{
			grid-template-columns:  repeat(auto-fit, minmax(352px, 1fr));
			gap: 24px;
		}
		&__img div {
			width: 352px;
		}
		.row1col2 {
			display: none;
		}
	}
	.form {
		padding: 40px 15px;
		&__title{
			font-size: 40px;
			line-height: 60px;
		}
		.form__buy {
			&__img {
				max-width: 444px; 
			}
			&__line {
				margin-bottom: 40px;
			}
			.form__amount {
			align-items: stretch;
			}
			.form__amount__title  {
				align-self: flex-start;
				margin-bottom: 25px;
			}
		}

		.form__tickets {
			max-width: 175px;
		}

	}
	.contacts {
		&__title{
			font-size: 40px;
			line-height: 60px;
	
		}
		&__line {
			margin-top: 0px;
			margin-bottom: 20px;
			}
			&__body {
				margin-top: 40px;
				justify-content: flex-start;
				flex-wrap: wrap;
			}
	
			&__text h2 {
				font-size: 20px;
				line-height: 23px;
	
			}
	
			&__text p {
				font-size: 18px;
				line-height: 21px;
				padding-bottom: 15px;
			}
			&__map {
				align-self: center;
				max-width: 728px;
				margin: 0;
			}

	}
		
	footer {
		background: $black;
		.footer__navigation{
			border-bottom: 3px solid #666666;
			&__container {
				padding: 65px 15px 50px 15px;
				display: flex;
				justify-content: space-between;
				flex-wrap: wrap;
			}
			&__icons {
				position: relative;
				right: 0;
				top: 0;
			}
			&__menu{
				padding:15px 0;
			}
			&__icons{
				align-self: flex-end;
			}
		}
	}
}

